import Head from "../components/head";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col } from "react-bootstrap";
const Index = () => (
  <html>
    <head />
    <body>
      <Container fluid={true} className="h100" style={{height: "100vh"}}>
        <Container className="h100" style = {{height: "100vh"}}>
          <Row className="row1 h100" style = {{height: "100vh",backgroundColor: "blue",alignItems: "center"}}>
            <Col lg={6}>
              <p className="pHello">Hello!</p>
              <p className="pHelloSub">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                eiusmod tempor incididunt ut labore et dolore magna aliqua.{" "}
              </p>
              <p className="pEmail">Email Address</p>
              <input
                type="text"
                className="txtEmail"
                placeholder="Enter email address here.."
              />
              <p className="pPassword">Password</p>
              <input
                type="password"
                className="txtEmail"
                placeholder="&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;"
              />

              <Row style={{ marginTop: "20px" }}>
                <Col lg={12}>
                  <input
                    className="inp-cbx"
                    id="cbx"
                    type="checkbox"
                    style={{ display: "none" }}
                  />
                  <label className="cbx" htmlFor="cbx">
                    <span>
                      <svg
                        viewbox="0 0 12 10"
                        style={{ height: "10px", width: "12px" }}
                      >
                        <polyline points="1.5 6 4.5 9 10.5 1"></polyline>
                      </svg>
                    </span>
                    <span className="lblRemember">Remember me</span>
                  </label>
                  <span className="lblForgot">Forgot password?</span> <br/>
                  <button className = "btnSignIn">Sign In</button>
                  <p className = "pDont">Don't have an account? <span>Sign Up</span></p>
                </Col>
              </Row>
            </Col>
            <Col lg={6}></Col>
          </Row>
        </Container>
      </Container>
      <style jsx>{`
        body,
        html {
          height: 100%;
          width: 100%;
        }
        .pHello {
          font-family: roboto, sans-serif;
          color: #212121;
          font-size: 2.5rem;
          font-weight: bold;
        }
        .pHelloSub {
          font-family: roboto, sans-serif;
          color: #9e9e9e;
          font-family: 1rem;
          margin-top: -10px;
        }
        .divcontainer-fluid,
        .divcontainer {
          height: 100vh;
        }
        .rowh100 {
          height: 100vh;
          background-color: blue;
        }
        .pEmail,
        .pPassword {
          font-family: roboto, sans-serif;
          color: #9e9e9e;
          font-size: 0.9rem;
        }
        .pPassword {
          margin-top: 20px;
        }
        .row1 {
          align-items: center !important;
        }
        .txtEmail {
          font-family: roboto, sans-serif;
          color: #424242;
          border: 1px solid #e0e0e0;
          background-color: #fafafa;
          border-radius: 5px;
          padding: 10px 20px;
          font-siz: 0.9rem;
          width: 400px;
          text-align: left;
          outline: none;
          transition: all 0.2s;
        }
        .txtEmail:focus {
          background-color: rgb(230, 49, 49);
          color: white;
        }
        input:focus::-webkit-input-placeholder {
          color: white;
        }
        input[type="password"],
        input[type="text"] {
          margin-top: -5px;
        }

        .cbx {
          margin: auto;
          -webkit-user-select: none;
          user-select: none;
          cursor: pointer;
        }
        .cbx span {
          display: inline-block;
          vertical-align: middle;
          transform: translate3d(0, 0, 0);
        }
        .cbx span:first-child {
          position: relative;
          width: 18px;
          height: 18px;
          border-radius: 3px;
          transform: scale(1);
          vertical-align: middle;
          border: 1px solid #9098a9;
          transition: all 0.2s ease;
        }
        .cbx span:first-child svg {
          position: absolute;
          top: 3px;
          left: 2px;
          fill: none;
          stroke: #ffffff;
          stroke-width: 2;
          stroke-linecap: round;
          stroke-linejoin: round;
          stroke-dasharray: 16px;
          stroke-dashoffset: 16px;
          transition: all 0.3s ease;
          transition-delay: 0.1s;
          transform: translate3d(0, 0, 0);
        }
        .cbx span:first-child:before {
          content: "";
          width: 100%;
          height: 100%;
          background: rgb(230, 49, 49);
          display: block;
          transform: scale(0);
          opacity: 1;
          border-radius: 50%;
        }
        .cbx span:last-child {
          padding-left: 8px;
        }
        .cbx:hover span:first-child {
          border-color: rgb(230, 49, 49);
        }

        .inp-cbx:checked + .cbx span:first-child {
          background:  rgb(230, 49, 49);
          border-color:  rgb(230, 49, 49);
          animation: wave 0.4s ease;
        }
        .inp-cbx:checked + .cbx span:first-child svg {
          stroke-dashoffset: 0;
        }
        .inp-cbx:checked + .cbx span:first-child:before {
          transform: scale(3.5);
          opacity: 0;
          transition: all 0.6s ease;
        }

        @keyframes wave {
          50% {
            transform: scale(0.9);
          }
        }
        .lblRemember {
          font-family: roboto, sans-serif;
          color: #424242;
          font-size: 0.9rem;
        }
        .lblRemember:hover {
          color: rgb(230, 49, 49);
        }
        .lblForgot {
          font-family: roboto, sans-serif;
          color: #9e9e9e;
          float: right;
          font-size: 0.9rem;
          cursor: pointer;
          transition: all 0.2s;
        }
        .lblForgot:hover {
          color: rgb(230, 49, 49);
        }
        .btnSignIn {
          font-family: roboto, sans-serif;
          color: white;
          font-size: 0.9rem;
          background: rgb(230, 49, 49);
          border: 0px;
          width: 170px;
          padding: 14px 10px;
          cursor: pointer;
          transition: all 0.2s;
          margin-top: 40px;
        }
        .pDont {
          font-family: roboto, sans-serif;
          color: #9e9e9e;
          font-size: 1rem;
          cursor: pointer;
          margin-top: 40px;
        }
        .pDont > span {
          color: rgb(230, 49, 49);
        }
      `}</style>
    </body>
  </html>
);

export default Index;
