import Head from "../components/head";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col } from "react-bootstrap";
const Index = () => (
  <html>
    <head />
    <body>
      <Container fluid={true} className="h100">
        <Container className="h100">
          <Row className="align-items-center h100">
            <Col lg={6}>
              <p className="pHello">Hello!</p>
              <p className="pHelloSub">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                eiusmod tempor incididunt ut labore et dolore magna aliqua.{" "}
              </p>
              <p className="pEmail">Email Address</p>
              <input
                type="text"
                className="txtEmail"
                placeholder="Enter email address here.."
              />
              <p className="pPassword">Password</p>
              <input
                type="password"
                className="txtEmail"
                placeholder="&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;&#8226;"
              />
              <input
                className="inp-cbx"
                id="cbx"
                type="checkbox"
                style={{display: "none"}}
              />
              <label className="cbx" htmlFor="cbx">
                <span>
                  <svg  viewbox="0 0 12 10" style={{height: "10px", width: "12px"}}>
                    <polyline points="1.5 6 4.5 9 10.5 1"></polyline>
                  </svg>
                </span>
                <span>CodePenChallenge</span>
              </label>
            </Col>
            <Col lg={6}></Col>
          </Row>
        </Container>
      </Container>
      <style jsx>{`
        body,
        html {
          height: 100%;
          width: 100%;
        }
        .pHello {
          font-family: roboto, sans-serif;
          color: #212121;
          font-size: 2.5rem;
          font-weight: bold;
        }
        .pHelloSub {
          font-family: roboto, sans-serif;
          color: #9e9e9e;
          font-family: 1rem;
          margin-top: -10px;
        }
        .divcontainer-fluid,
        .divcontainer {
          height: 100vh;
        }
        .rowh100 {
          height: 100vh;
          background-color: blue;
        }
        .pEmail,
        .pPassword {
          font-family: roboto, sans-serif;
          color: #9e9e9e;
          font-size: 0.9rem;
        }
        .pPassword {
          margin-top: 20px;
        }
        .txtEmail {
          font-family: roboto, sans-serif;
          color: #424242;
          border: 1px solid #e0e0e0;
          background-color: #fafafa;
          border-radius: 10px;
          padding: 10px 20px;
          font-siz: 0.9rem;
          width: 300px;
          text-align: left;
          outline: none;
          transition: all 0.2s;
        }
        .txtEmail:focus {
          background-color: rgba(230, 49, 49);
          color: white;
        }
        input:focus::-webkit-input-placeholder {
          color: white;
        }
        input[type="password"],
        input[type="text"] {
          margin-top: -5px;
        }
      `}</style>
    </body>
  </html>
);

export default Index;
